import React, {useState, useEffect} from 'react';
import { BiMenu, BiX } from "react-icons/bi";
import {Button} from '../../Globalstyles';

import {
    FooterSection,
    FooterContainer,
    FooterNewsletter,
    FooterNewsletterTitle,
    FooterNewsletterText,
    FooterNewsletterForm,
    FooterNewsletterInput,
    FooterBtn,
    FooterLinkContainer,
    FooterLinksWrapper,
    FooterLinks,
    FooterLinkTitle,
    FooterLink,
    FooterCopyRight,
    FooterLabel

} from './Footer.styles';
import LOGO from '../../images/logo.png';
const Footer = () => {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);
    const handleClick = () => setClick(!click);
    const closeMenu = () => setClick(false);

    const showButton = () =>{
        if(window.innerWidth<= 1000){
            setButton(false);
        }else{
            setButton(true);
        }
    }

    useEffect(() => {
        showButton();
    }, []);
    window.addEventListener('resize', showButton);

    return (
        <div>
            <FooterSection>
                <FooterContainer>
                <NavLogo to="/">


                       INSTITUTO NUEVA GRANADA
                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                    <FooterNewsletter>
                        <FooterNewsletterTitle>INSTITUTO EDUCATIVO NUEVA GRANADA</FooterNewsletterTitle>

                        <FooterNewsletterText>CALLE 33 SUR NO 24-48</FooterNewsletterText>
                        <FooterNewsletterText>TEL:8642992</FooterNewsletterText>

                    </FooterNewsletter>   
                    <FooterLinkContainer>

                    </FooterLinkContainer>
                    pagina realizada por jhon soledad y sara rojas 

                </FooterContainer>
            </FooterSection>

        </div>
    )
}

export default Footer;
