import React, {useState, useEffect} from 'react';
import { BiMenu, BiX } from "react-icons/bi";
import {Button} from '../../Globalstyles';

import {
    FooterSection,
    FooterContainer,
    FooterNewsletter,
    FooterNewsletterTitle,
    FooterNewsletterText,
    FooterNewsletterForm,
    FooterNewsletterInput,
    FooterBtn,
    FooterLinkContainer,
    FooterLinksWrapper,
    FooterLinks,
    FooterLinkTitle,
    FooterLink,
    FooterCopyRight,
    NavLogo,
    MenuIcon,
    Imgw,
    FooterLabel

} from './Footer.styles';
import LC from '../../images/LC.jpg';
const Footer = () => {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);
    const handleClick = () => setClick(!click);
    const closeMenu = () => setClick(false);

    const showButton = () =>{
        if(window.innerWidth<= 1000){
            setButton(false);
        }else{
            setButton(true);
        }
    }

    useEffect(() => {
        showButton();
    }, []);
    window.addEventListener('resize', showButton);

    return (
        <div>
            <FooterSection>
                <FooterContainer>
                <FooterLinkContainer>
                <NavLogo to="profesor">
                <Imgw src={LC}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>

                 <NavLogo to="/">
                <Imgw src={LC}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                 </FooterLinkContainer>
                    <FooterNewsletter>
                    <FooterNewsletterTitle></FooterNewsletterTitle>
                        <FooterNewsletterTitle>INSTITUTO EDUCATIVO NUEVA GRANADA</FooterNewsletterTitle>

                        <FooterNewsletterText>CALLE 33 SUR NO 24-48</FooterNewsletterText>
                        <FooterNewsletterText>TEL:8642992</FooterNewsletterText>

                    </FooterNewsletter>   
                    <FooterLinkContainer>

                    </FooterLinkContainer>
                    pagina realizada por jhon soledad y sara rojas 

                </FooterContainer>
            </FooterSection>

        </div>
    )
}

export default Footer;
