import {OutlineButton} from '../../Globalstyles';
import React, {useState, useEffect} from 'react';
import { BiMenu, BiX } from "react-icons/bi";
import {Button} from '../../Globalstyles';

import {
    RecipeContainer,
    RecipeWrapper,
    RecipeTitle,
    WorksContainer,
    WorksContent,
    WorksCardContent,
    WorksCard,
    WorksIconContainer,
    Imge,
    WorksCardTitle,
    MenuItem,
    MenuIcon,
    MenuLink,
    WorksCardText

    
    
   
  



} from './Profesores.styles';
import P1 from '../../images/foto.jpg';
import P2 from '../../images/foto2.jpg';
import P3 from '../../images/foto3.jpg';


const Profesores = () => {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);
    const handleClick = () => setClick(!click);
    const closeMenu = () => setClick(false);

    const showButton = () =>{
        if(window.innerWidth<= 1000){
            setButton(false);
        }else{
            setButton(true);
        }
    }

    useEffect(() => {
        showButton();
    }, []);
    window.addEventListener('resize', showButton);

    return (
        <div>
        <WorksContent>
            <WorksContainer>
            <RecipeTitle>NUESTRA COMUNIDAD</RecipeTitle>
                <WorksCardContent>
                <WorksCard>
                    <MenuIcon onClick={handleClick}>
                            {click ? <BiX/> : <BiMenu/>}
                        </MenuIcon>
                                <MenuLink onClick={closeMenu} to="directiva"><Imge src={P1}/> </MenuLink>
                                <WorksCardText>DIRECTIVOS</WorksCardText>
                    
                    </WorksCard>
                    <WorksCard>
                         <WorksCard>
                    <MenuIcon onClick={handleClick}>
                            {click ? <BiX/> : <BiMenu/>}
                        </MenuIcon>
                                <MenuLink onClick={closeMenu} to="maestro"><Imge src={P1}/> </MenuLink>
                                <WorksCardText>PROFESORES</WorksCardText>
                    </WorksCard>
                    </WorksCard>
                    <WorksCard>
                    <MenuIcon onClick={handleClick}>
                            {click ? <BiX/> : <BiMenu/>}
                        </MenuIcon>
                                <MenuLink onClick={closeMenu} to="order"><Imge src={P1}/> </MenuLink>
                                <WorksCardText>ESTUDIANTES</WorksCardText>
                    </WorksCard>
                </WorksCardContent>

          
            </WorksContainer>
        </WorksContent>        
        
       
        
                
    </div>
    );
}

export default Profesores;
