import React, {useState, useEffect} from 'react';
import { BiMenu, BiX } from "react-icons/bi";
import {Button} from '../../Globalstyles';

import {
    FooterSection,
    FoterSection,
    FooterContainer,
    FooterNewsletter,
    FooterNewsletterTitle,
    FooterNewsletterText,
    FooterNewsletterForm,
    FooterNewsletterInput,
    FooterBtn,
    FooterLinkContainer,
    FooterLinksWrapper,
    FooterLinks,
    FooterLinkTitle,
    FooterLink,
    FooterCopyRight,
    NavLogo,
    MenuIcon,
    WorksCardContent,
    Imgw,
    WorksCard,
    FoterContainer,
    FooterLabel

} from './Footer.styles';
//import LOGO from '../../images/LOGO.jpg';
import LC from '../../images/LC.jpg';
import LN from '../../images/LN.png';
import LNC from '../../images/LNC.png';
import LR from '../../images/LR.jpg';
import LM from '../../images/LM.png';
import LH from '../../images/LH.jpg';
import LE from '../../images/LE.jpg';
import LD from '../../images/LH.jpg';
import LP from '../../images/LP.png';

const Footer = () => {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);
    const handleClick = () => setClick(!click);
    const closeMenu = () => setClick(false);

    const showButton = () =>{
        if(window.innerWidth<= 1000){
            setButton(false);
        }else{
            setButton(true);
        }
    }

    useEffect(() => {
        showButton();
    }, []);
    window.addEventListener('resize', showButton);

    return (
        <div>
             <FoterSection>
             <FoterContainer>
            <WorksCardContent>


            <WorksCard>
                <NavLogo to="/">
                <Imgw src={LOGO}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                 </WorksCard>



                <WorksCard>
                <NavLogo to="noticia">
                <Imgw src={LN}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                 </WorksCard>


                 <WorksCard>
                <NavLogo to="profesor">
                <Imgw src={LNC}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                 </WorksCard>


                 <WorksCard>
                <NavLogo to="/">
                <Imgw src={LC}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                 </WorksCard>


                 <WorksCard>
                <NavLogo to="recipe">
                <Imgw src={LR}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                 </WorksCard>


                 <WorksCard>
                <NavLogo to="directiva">
                <Imgw src={LM}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                 </WorksCard>


                 <WorksCard>
                <NavLogo to="horario">
                <Imgw src={LH}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                 </WorksCard>


                 <WorksCard>
                <NavLogo to="entra">
                <Imgw src={LE}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                 </WorksCard>

                 <WorksCard>
                <NavLogo to="order">
                <Imgw src={LP}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                 </WorksCard>

                 <WorksCard>
                <NavLogo to="maestro">
                <Imgw src={LM}/>

                 </NavLogo>
                 <MenuIcon onClick={handleClick}>
                     {click ? <BiX/> : <BiMenu/>}
                 </MenuIcon>
                 </WorksCard>




                 </WorksCardContent>
                 </FoterContainer>
                 </FoterSection>

            <FooterSection>

                <FooterContainer>

                    <FooterNewsletter>
                    <FooterNewsletterTitle></FooterNewsletterTitle>
                        <FooterNewsletterTitle>INSTITUTO EDUCATIVO NUEVA GRANADA</FooterNewsletterTitle>

                        <FooterNewsletterText>CALLE 33 SUR NO 24-48</FooterNewsletterText>
                        <FooterNewsletterText>TEL:8642992</FooterNewsletterText>

                    </FooterNewsletter>   
                    <FooterLinkContainer>

                    </FooterLinkContainer>
                    pagina realizada por jhon soledad y sara rojas 

                </FooterContainer>
            </FooterSection>

        </div>
    )
}

export default Footer;
