import ChickenImg from '../../images/Big-chicken.svg';
import React, {useState, useEffect} from 'react';
import { BiMenu, BiX } from "react-icons/bi";
import {OutlineButton} from '../../Globalstyles';
import {Button} from '../../Globalstyles';
import {
    MealsContainer,
    MealsWrapper,
    MealsImage,
    Img,
    MealsContent,
    MealsContentTitle,
    MealsContentText,
    MealsContentItems,
    MealsContentList,
    MealsContentDetails,
    MealsContentIcon,
    MealsContentCategory,
    MealsContentBtn,


    MenuItemBtn,
    MenuLinkBtn,
    WorksCardText
} from './Meals.styles';
function Meals() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);
    const handleClick = () => setClick(!click);
    const closeMenu = () => setClick(false);

    const showButton = () =>{
        if(window.innerWidth<= 1000){
            setButton(false);
        }else{
            setButton(true);
        }
    }

    useEffect(() => {
        showButton();
    }, []);
    window.addEventListener('resize', showButton);
    return (
        <div>
            <MealsContentTitle>FORMULARIO DE INSCRIPCION </MealsContentTitle>
           <MealsContent>

                        <MealsContentText>
                        <form>
        <label>
         Nombres:
         <input type="text" name="nombre" />
         </label>

         </form>
         <form>
        <label>
         Apellidos:
         <input type="text" name="apellido" />
         </label>

         </form>

       <form>
        <label>
         identificacion:
         <input type="text" name="numero de identificacion" />
         </label>

         </form>
         <form>
        <label>
         codigo:
         <input type="text" name="codigo" />
         </label>

         </form>
         <form>
        <label>
         contraseña         :
         <input type="text" name="Contraseña" />

         </label>
         <form>
        <label>
         confirme contraseña :
         <input type="text" name="cofirme contraseña" />
         </label>

         </form>

         </form>

                        </MealsContentText>


                        <MenuItemBtn>
                                {button?(
                                    <MenuLinkBtn to="/profesor">
                                        <Button primary>REGUISTRAR</Button>
                                    </MenuLinkBtn>
                                ): (
                                    <MenuLinkBtn to="/profesor">
                                        <Button primary bigFont onClick={closeMenu}>REGUISTRAR</Button>
                                    </MenuLinkBtn>
                                )
                                }


                            </MenuItemBtn>
                        </MealsContent>       


        </div>
    )
}

export default Meals;
