const Recipes = () => {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);
    const handleClick = () => setClick(!click);
    const closeMenu = () => setClick(false);

    const showButton = () =>{
        if(window.innerWidth<= 1000){
            setButton(false);
        }else{
            setButton(true);
        }
    }

    useEffect(() => {
        showButton();
    }, []);
    window.addEventListener('resize', showButton);
    return (
        <div>


        <WorksContent>
        <RecipeTitle>DIRECTIVAS</RecipeTitle>
            <WorksContainer>

                <WorksCardContent>

                    <WorksCard>
                        <Imge src={P1}/> 
                        <WorksCardTitle>  </WorksCardTitle>
                        <WorksCardTitle>JHON SOLEDAD</WorksCardTitle>
                        <WorksCardText>
                            Maestro de Fisica.
                            tel:3208628163
                        </WorksCardText>
                    </WorksCard>

                    <WorksCard>
                        <Imge src={P2}/>  
                        <WorksCardTitle>SARA ROJAS</WorksCardTitle>
                        <WorksCardText>
                           maestra de quimica. 
                        </WorksCardText>
                    </WorksCard>
                    <WorksCard>
                        <Imge src={P3}/> 
                        <WorksCardTitle>ERIKA GONZALEZ</WorksCardTitle>
                        <WorksCardText>
                           maestra de artes.
                        </WorksCardText>
                    </WorksCard>
                    <WorksCard>
                        <Imge src={P3}/> 
                        <WorksCardTitle>ERIKA GONZALEZ</WorksCardTitle>
                        <WorksCardText>
                           maestra de artes.
                        </WorksCardText>
                    </WorksCard>
                </WorksCardContent>
            </WorksContainer>
        <WorksContainer>

                <WorksCardContent>
                    <WorksCard>
                        <Imge src={P7}/> 
                        <WorksCardTitle>  </WorksCardTitle>
                        <WorksCardTitle>JHON SOLEDAD</WorksCardTitle>
                        <WorksCardText>
                            Maestro de Fisica.
                            tel:3208628163
                        </WorksCardText>
                    </WorksCard>

                    <WorksCard>
                        <Imge src={P8}/>  
                        <WorksCardTitle>SARA ROJAS</WorksCardTitle>
                        <WorksCardText>
                           maestra de quimica. 
                        </WorksCardText>
                    </WorksCard>
                    <WorksCard>
                        <Imge src={P9}/> 
                        <WorksCardTitle>ERIKA GONZALEZ</WorksCardTitle>
                        <WorksCardText>
                           maestra de artes.
                        </WorksCardText>
                    </WorksCard>
                    <WorksCard>
                        <Imge src={P9}/> 
                        <WorksCardTitle>ERIKA GONZALEZ</WorksCardTitle>
                        <WorksCardText>
                           maestra de artes.
                        </WorksCardText>
                    </WorksCard>
                </WorksCardContent>
            </WorksContainer>
        </WorksContent>
        <WorksContent>
        <WorksContent>
        </WorksContent>
        </WorksContent>
        <WorksContent>
        <MenuItemBtn>
                                {button?(
                                    <MenuLinkBtn to="/profesor">
                                        <Button primary>VOLVER</Button>
                                    </MenuLinkBtn>
                                ): (
                                    <MenuLinkBtn to="/profesor">
                                        <Button primary bigFont onClick={closeMenu}>VOLVER</Button>
                                    </MenuLinkBtn>
                                )
                                }


                            </MenuItemBtn>
                            </WorksContent>
    </div>
    );
}

export default Recipes;
