import {OutlineButton} from '../../Globalstyles';
import React, {useState, useEffect} from 'react';
import { BiMenu, BiX } from "react-icons/bi";
import {Button} from '../../Globalstyles';
import {
    RecipeContainer,
    RecipeWrapper,
    RecipeTitle,
    WorksContainer,
    WorksContent,
    WorksCardContent,
    WorksCard,
    WorksIconContainer,
    Imge,
    WorksCardTitle,
    MenuItemBtn,
    MenuLinkBtn,
    WorksCardText







} from './Recipes.styles';
import P1 from '../../images/foto.jpg';
import P2 from '../../images/foto2.jpg';
import P3 from '../../images/foto3.jpg';
import P4 from '../../images/foto4.jpg';
import P5 from '../../images/foto5.jpg';
import P6 from '../../images/foto6.jpg';
import P7 from '../../images/foto7.jpg';
import P8 from '../../images/foto8.jpg';
import P9 from '../../images/foto9.jpg';
import P10 from '../../images/foto10.jpg';
import P11 from '../../images/foto11.jpg';
import P12 from '../../images/foto12.jpg';
const Recipes = () => {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);
    const handleClick = () => setClick(!click);
    const closeMenu = () => setClick(false);

    const showButton = () =>{
        if(window.innerWidth<= 1000){
            setButton(false);
        }else{
            setButton(true);
        }
    }

    useEffect(() => {
        showButton();
    }, []);
    window.addEventListener('resize', showButton);
    return (
        <div>


        <WorksContent>
        <RecipeTitle>ESTUDIANTES</RecipeTitle>
        <WorksCardTitle>Estudiantes que por su depempeño estudiantil merecen estar en el cuadro de honor de la </WorksCardTitle>
        <WorksCardTitle>INSTITUCION NUEVA GRANADA</WorksCardTitle>


            <WorksContainer>
            <Imge src={P12}/> 

            <WorksCardTitle>PRIMERO</WorksCardTitle>
                <WorksCardContent>

                    <WorksCard>
                        <Imge src={P1}/> 
                        <WorksCardTitle>  </WorksCardTitle>
                        <WorksCardTitle>JHON SOLEDAD</WorksCardTitle>
                        <WorksCardText>
                            Maestro de Fisica.
                            tel:3208628163
                        </WorksCardText>
                    </WorksCard>

                    <WorksCard>
                        <Imge src={P2}/>  
                        <WorksCardTitle>SARA ROJAS</WorksCardTitle>
                        <WorksCardText>
                           maestra de quimica. 
                        </WorksCardText>
                    </WorksCard>
                    <WorksCard>
                        <Imge src={P3}/> 
                        <WorksCardTitle>ERIKA GONZALEZ</WorksCardTitle>
                        <WorksCardText>
                           maestra de artes.
                        </WorksCardText>
                    </WorksCard>

                </WorksCardContent>
            </WorksContainer>

        </WorksContent>
        <WorksContent>
        <WorksContent>
        </WorksContent>
        </WorksContent>
        <WorksContent>
        <MenuItemBtn>
                                {button?(
                                    <MenuLinkBtn to="/profesor">
                                        <Button primary>VOLVER</Button>
                                    </MenuLinkBtn>
                                ): (
                                    <MenuLinkBtn to="/profesor">
                                        <Button primary bigFont onClick={closeMenu}>VOLVER</Button>
                                    </MenuLinkBtn>
                                )
                                }


                            </MenuItemBtn>
                            </WorksContent>
    </div>
    );
}

export default Recipes;
